 // we got an average // Add the newest measurement to an array // and subtract the oldest measurement from the array // to maintain a sum of last measurements sumRED -= readsRED[ptr]; sumRED += reader; readsRED[ptr] = reader; lastRED = sumRED / samp_siz; // // R CALCULATION // save all the samples of a period both for IR and for RED readsIRMM[ptrMM]=lastIR; readsREDMM[ptrMM]=lastRED; ptrMM++; ptrMM %= maxperiod_siz; samplesCounter++; // // if I've saved all the samples of a period, look to find // max and min values and calculate R parameter if(samplesCounter>=samples){ samplesCounter =0; IRmax = 0; IRmin=1023; REDmax = 0; REDmin=1023; for(int i=0;i<maxperiod_siz;i++) { if( readsIRMM[i]> IRmax) IRmax = readsIRMM[i]; if( readsIRMM[i]>0 && readsIRMM[i]< IRmin ) IRmin = readsIRMM[i]; readsIRMM[i] =0; if( readsREDMM[i]> REDmax) REDmax = readsREDMM[i]; if( readsREDMM[i]>0 && readsREDMM[i]< REDmin ) REDmin = readsREDMM[i]; readsREDMM[i] =0; } R = ( (REDmax-REDmin) / REDmin) / ( (IRmax-IRmin) / IRmin ) ; } // check that the finger is placed inside // the sensor. If the finger is missing // RED curve is under the IR. // if (lastRED < lastIR) { if(finger_status==true) { finger_status = false; // lcd.clear(); // lcd.setCursor(0,0); // lcd.print(\\"No finger?\\"); //Serial.println(\\"No finger?\\"); } } else { if(finger_status==false) { // lcd.clear(); finger_status = true; //lcd.setCursor(10,0); //lcd.print(\\"c=\\"); //Serial.println(\\"c\\"); //lcd.setCursor(0,0); //lcd.print(\\"bpm\\"); // lcd.setCursor(0,1); // lcd.print(\\"SpO\\"); lcd.write(1); //2 // lcd.setCursor(10,1); // lcd.print(\\"R=\\"); } } float avR = 0; avBPM=0; if (finger_status==true){ // lastIR holds the average of the values in the array // check for a rising curve (= a heart beat) if (lastIR > beforeIR) { rise_count++; // count the number of samples that are rising if (!rising && rise_count > rise_threshold) { // lcd.setCursor(3,0); // lcd.write( 0 ); // <3 // Ok, we have detected a rising curve, which 