e.Videos.Streams.GetManifestAsync(video.Id, cancellationToken); var audioStreamInfo = streamInfo.GetAudioOnlyStreams().GetWithHighestBitrate(); // CancellationToken kontrolü sonrasında yeni Video nesnesi oluşturulacak if (cancellationToken.IsCancellationRequested) { throw new OperationCanceledException(cancellationToken); } // Video nesnesinin oluşturulması return new Video { Id = video.Id, Title = video.Title, Author = video.Author.Title, ThumbnailUrl = video.Thumbnails.GetWithHighestResolution()?.Url, Duration = video.Duration?.ToString(), Likes = (int)videoDetails.Engagement.LikeCount, ViewCount = (int)videoDetails.Engagement.ViewCount, StreamUrl = audioStreamInfo?.Url, AudioStreamInfo = audioStreamInfo }; // Semaphore release işlemi çağıran metod tarafından yapılacak } [Obsolete] private async Task GetVideoDetailsAsync(YoutubeExplode.Playlists.PlaylistVideo videoPlaylistResult) { try { var videoDetails = await youtube.Videos.GetAsync(videoPlaylistResult.Id); var streamInfo = await youtube.Videos.Streams.GetManifestAsync(videoPlaylistResult.Id); var audioStreamInfo = streamInfo.GetAudioOnlyStreams().GetWithHighestBitrate(); MyCollection.Add(new Video { Id = videoPlaylistResult.Id, Title = videoPlaylistResult.Title, Author = videoPlaylistResult.Author.Title, ThumbnailUrl = videoPlaylistResult.Thumbnails.GetWithHighestResolution()?.Url, Duration = videoPlaylistResult.Duration.ToString(), Likes = (int)videoDetails.Engagement.LikeCount, // Beğeni sayısını ayarla ViewCount = (int)videoDetails.Engagement.ViewCount, // Beğenmeme sayısını ayarla StreamUrl = audioStreamInfo?.Url, // Akış URL'sini ayarla AudioStreamInfo = audioStreamInfo// Akış URL'sini ayarla }); } finally { semaphore.Release(); // Release the slot } } public event PropertyChangedEventHandler PropertyChanged; protected virtual void OnPropertyChanged([CallerMemberName] string propertyName = null) { PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(propertyName)); } } public class Video : INotifyPr