5; const zoom = 1; const offsetX = 0.5; const offsetY = 0.5; let angleX = 0; let angleY = 0; let angleZ = 0; let bestIndex = -1; let bestDistance = Infinity; let startNewEdgeIndex = -1; let isMouseDown = false; let prevMousePos = null; const gridSpacing = 50; const gridPoints = []; let displayGrid = true; function roundToGrid(value) { return Math.round(value / gridSpacing) * gridSpacing; } function snapToGrid(x, y) { return { x: roundToGrid(x - canvas.getBoundingClientRect().left), y: roundToGrid(y - canvas.getBoundingClientRect().top) }; } // Red Dot const redDot = document.getElementById('red-dot'); const greenDotContainer = document.getElementById('green-dot'); document.body.appendChild(greenDotContainer); document.getElementById('add-edge').addEventListener('click', () => { if (bestIndex === -1) return; if (startNewEdgeIndex === -1) { const {x, y} = snapToGrid(window.event.clientX, window.event.clientY); const newVertexX = (x - canvas.width / 2) / (canvas.height * scale) + offsetX; const newVertexY = (y - canvas.height / 2) / (canvas.height * scale) + offsetY; const newVertexZ = 0; vertices.push([newVertexX.toPrecision(4), newVertexY.toPrecision(4), newVertexZ]); startNewEdgeIndex = vertices.length - 1; } else { edges.push([startNewEdgeIndex, bestIndex]); startNewEdgeIndex = -1; } }); // Remove Edge document.getElementById('remove-edge').addEventListener('click', () => { if (bestIndex === -1) return; edges.forEach((edge, index) => { if (edge.includes(bestIndex)) { edges.splice(index, 1); } }); }); function rotateX(angle) { const c = Math.cos(angle); const s = Math.sin(angle); return [ [1, 0, 0], [0, c, -s], [0, s, c], ]; } function rotateY(angle) { const c = Math.cos(angle); const s = Math.sin(angle); return [ [c, 0, s], [0, 1, 0], [-s, 0, c], ]; } function rotateZ(angle) { const c = Math.cos(angle); const s = Math.sin(angle); return [ [c, -s, 0], [s, c, 0], [0, 0, 1], ]; } function project(vertex, scale, offsetX, offsetY, zoom) { const [x, y, z] = vertex; const